import React from 'react';
import { Link } from "react-router-dom";
import {Home} from '../Components/Pages';
export class Menu extends React.Component {
    constructor(props){
        super(props);
        this.state={
            serch:''
        }
        
    }
    changeHandler = (e)=>{
        this.setState({ [e.target.name]:e.target.value })
    }

    handleClick=()=>{
        alert(this.state.serch);
        this.setState(<Home name={this.state.serch}/>)
        .then(this.props.history.push('/home'))

    }

    render(){
        return (
            <>
                <div>
                    <nav className="navbar navbar-expand-md bg-dark navbar-dark">
                        <span className="navbar-brand">Sparrow</span>
                        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsibleNavbar">
                            <span className="navbar-toggler-icon"></span>
                        </button>
                        <div className="collapse navbar-collapse" id="collapsibleNavbar">
                            <ul className="navbar-nav">
                                <li className="nav-item">
                                    <span className="nav-link" ><Link className="navbar-nav" to="/home">Home</Link></span>
                                </li>
                                <li className="nav-item">
                                    <span className="nav-link" ><Link className="navbar-nav" to="/upload">Upload</Link></span>
                                </li>
                            </ul>
    
                            <div class="form-inline">
                                <input class="form-control form-control-sm mr-sm-2" name="serch" value={this.state.serch} onChange={this.changeHandler} type="text" placeholder="Search..." />
                                <button class="btn btn-outline-primary btn-sm" type="submit" onClick={this.handleClick}>Search</button>
                            </div>
    
                        </div>
    
                    </nav>
                </div>
            </>
        )
    }
    
}
