    constructor(props) {
        super(props);
        this.state = {
            item: [],
            pic:''
        }
    }

    componentDidMount() {
        let {id}=this.props.match.params;
        axios.get('http://127.0.0.1:8000/file/?title='+id)
            .then(res => {
                this.setState({ item: res.data });
            })
            .catch(function (error) {
                console.log(error)
            });
