                            <br/>
                            <div className="card" style={{width:'350px'}}>
                                <img className="card-img-top" src={e.content} alt={e.image} style={{width:'100%'}} />
                                <div className="card-body">
                                    <h4 className="card-title">{e.title}</h4>
