import React, { useEffect, useState } from "react";
import { useHistory } from 'react-router-dom';
import axios from 'axios';
import ApiUrl from '../Components/Api';

export const CreateUser = () => {
    const [user, setUser] = useState({
        username: '',
        email: '',
        mobile: '',
        address: '',
        selectCountry: [],
        selectState: [],
        selectCity: [],
    });

    useEffect(() => {
        loadData()
    }, []);

    const loadData = async (e) => {
        const result = await axios.get('http://127.0.0.1:8000/country/');
        setUser({
            ...user, selectCountry: result.data
        });
        console.log(user.selectCountry);
    }
    const handleChange = (e) => {
        setUser({
            ...user, [e.target.name]: e.target.value
        })
    };
    const countrychange = async (e) => {
        const result = await axios.get(`http://127.0.0.1:8000/state/?country_id=${e.target.value}`);
        setUser({ ...user, selectState: result.data,
        country_id: e.target.value
        });
        console.log(user.selectState);
    }

    const changeState = async (e) => {
        const result = await axios.get(`http://127.0.0.1:8000/city/?state_id=${e.target.value}`);
        setUser({ ...user, selectCity: result.data, state_id: e.target.value});
        console.log(user.selectCity);
    }
    const changeCity = async (e) =>{
        setUser({...user, city_id: e.target.value})
    }



    return (
        <>
            <div className="container">
                <div className="row">
                    <div className="col-md-4">
                        <label>Name</label>
                        <input type="text" className='form-control form-control-sm' name='username'
                            placeholder='Name' id='username' value={user.username}
                            onChange={handleChange} required />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-4">
                        <label>Email</label>
                        <input type="text" className='form-control form-control-sm' name='email'
                            placeholder='Email' id='email' value={user.email}
                            onChange={handleChange} required />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-4">
                        <label>Mobile</label>
                        <input type="text" className='form-control form-control-sm' name='mobile'
                            placeholder='Mobile' id='mobile' value={user.mobile}
                            onChange={handleChange} required />
                    </div>
                </div>
                <div className="row">
                    <div className="col-md-4">
                        <label>Address</label>
                        <input type="text" className='form-control form-control-sm' name='address'
                            placeholder='Address' id='address' value={user.address}
                            onChange={handleChange} required />
                    </div>
                </div>

                <div className="row">
                    <div className="col-md-4">
                        <label>Country</label>
                        <select className="custom-select custom-select-sm" name="country_id" onChange={e => countrychange(e)}>
                            <option>--Select--</option>
                            {user.selectCountry.map((e, key) => {
                                return <option key={key} value={e.id}>{e.country_name}</option>
                            })}
                        </select>
                    </div>
                </div>
                
                <div className="row">
                    <div className="col-md-4">
                        <label>State</label>
                        <select className="custom-select custom-select-sm" name="state_id" onChange={changeState}>
                            <option>--Select--</option>
                            {user.selectState.map((e, key) => {
                                return <option key={key} value={e.id}>{e.state_name}</option>
                            })}
                        </select>
                    </div>
                </div>

                <div className="row">
                    <div className="col-md-4">
                        <label>City</label>
                        <select className="custom-select custom-select-sm" name="city_id" onChange={changeCity}>
                            <option>--Select--</option>
                            {user.selectCity.map((e, key) => {
                                return <option key={key} value={e.id}>{e.city_name}</option>
                            })}
                        </select>
                    </div>
                </div>

                
            </div>
        </>
    )
}
