import React, { Component } from 'react';
// import { Link } from 'react-router-dom';
import axios from 'axios';

export class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            item: []
        }
    }

    componentDidMount() {
        axios.get('http://127.0.0.1:8000/posts/')
            .then(res => {
                this.setState({ item: res.data });
            })
            .catch(function (error) {
                console.log(error)
            })
    }
    render() {
        return (
            <>
                <br />
                <div className="container">
                    <br />
                    <div className='row'>
                    {this.state.item.map((item) =>
                    <div key={item.id}>
                        <div className='col-md-12'>
                            <div class="card" style="width:400px">
                                <img class="card-img-top" src="img_avatar1.png" alt="Card image" style="width:100%" />
                                <div class="card-body">
                                    <h4 class="card-title">John Doe</h4>
                                    <p class="card-text">Some example text some example text. John Doe is an architect and engineer</p>
                                    <a href="#" class="btn btn-primary">See Profile</a>
                                </div>
                            </div>
                        </div>
                        </div>
                )}
                    </div>
                </div>
                {this.state.item.map((item) =>

                    <div key={item.id}>
                        <img src={item.image}></img>
                        <span>{item.title}</span>
                    </div>
                )}
            </>
        )
    }
}

export class UploadFile extends Component {
    constructor(props) {
        super(props);
        this.state = {
            title: '',
            content: '',
            image: null
        }

        this.state.handleChange = (e) => {
            this.setState({
                [e.target.id]: e.target.value
            })
        };

        this.state.handleImageChange = (e) => {
            this.setState({
                image: e.target.files[0]
            })
        };

        this.state.handleSubmit = (e) => {
            e.preventDefault();
            console.log(this.state);
            let form_data = new FormData();
            form_data.append('image', this.state.image, this.state.image.name);
            form_data.append('title', this.state.title);
            form_data.append('content', this.state.content);
            let url = 'http://127.0.0.1:8000/posts/';
            axios.post(url, form_data, {
                headers: {
                    'content-type': 'multipart/form-data'
                }
            })
                .then(res => {
                    console.log(res.data);
                })
                .catch(err => console.log(err))
        };
    }
    render() {
        return (
            <>
                <div className="container">
                    <hr />
                    <form onSubmit={this.state.handleSubmit}>
                        <div className='row'>
                            <div className='col-md-4'>
                                <label>Title</label>
                                <input type="text" className='form-control form-control-sm' placeholder='Title' id='title' value={this.state.title} onChange={this.state.handleChange} required />
                                <hr />
                            </div>
                        </div>

                        <div className='row'>
                            <div className='col-md-4'>
                                <label>Content</label>
                                <input type="text" className='form-control form-control-sm' placeholder='Content' id='content' value={this.state.content} onChange={this.state.handleChange} required />
                                <hr />
                            </div>
                        </div>

                        <div className='row'>
                            <div className='col-md-4'>
                                <input type="file" id="image" accept="image/png, image/jpeg" onChange={this.state.handleImageChange} required />
                                <hr />
                            </div>
                        </div>

                        <div className='row'>
                            <div className='col-md-4'>
                                <input type="submit" className='btn btn-outline-info' />
                            </div>
                        </div>
                    </form>
                </div>
            </>
        )
    }
}
